INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (3, 'Star Wars Jedi: Fallen Order', 'A galaxy-spanning adventure awaits in Star Wars Jedi: Fallen Order, a 3rd person action-adventure title from Respawn. An abandoned Padawan must complete his training, develop new powerful Force abilities, and master the art of the lightsaber - all while staying one step ahead of the Empire.', 100, 9900, false, '2020-05-03 00:25:03.000000', 'https://steamcdn-a.akamaihd.net/steam/apps/1172380/header.jpg?t=1588279360', 2);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (5, 'Borderlands 3', 'The original shooter-looter returns, packing bazillions of guns and a mayhem-fueled adventure! Blast through new worlds & enemies and save your home from the most ruthless cult leaders in the galaxy', 100, 8500, false, '2020-05-03 00:29:06.000000', 'https://steamcdn-a.akamaihd.net/steam/apps/397540/header.jpg?t=1587675179', 2);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (8, 'Grand Theft Auto V', 'Grand Theft Auto V for PC offers players the option to explore the award-winning world of Los Santos and Blaine County in resolutions of up to 4k and beyond, as well as the chance to experience the game running at 60 frames per second.', 500, 8999, false, '2020-05-03 00:31:07.000000', 'https://steamcdn-a.akamaihd.net/steam/apps/271590/header.jpg?t=1586539531', 4);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (9, 'Mount & Blade II: Bannerlord', 'The horns sound, the ravens gather. An empire is torn by civil war. Beyond its borders, new kingdoms rise. Gird on your sword, don your armour, summon your followers and ride forth to win glory on the battlefields of Calradia. Establish your hegemony and create a new world out of the ashes of the old', 547, 7890, false, '2020-05-03 00:33:26.000000', 'https://steamcdn-a.akamaihd.net/steam/apps/261550/header.jpg?t=1586885335', 4);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (4, 'Assassin''s Creed Odyssey', 'Choose your fate in Assassin''s Creed® Odyssey. From outcast to living legend, embark on an odyssey to uncover the secrets of your past and change the fate of Ancient Greece.', 10, 2000, false, '2020-05-03 00:27:03.000000', 'https://steamcdn-a.akamaihd.net/steam/apps/812140/header.jpg?t=1588279220', 2);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (6, 'Streets of Rage 4', 'Amongst the best beat’em up series ever created, jammin’ ‘90s beats and over the top street fighting, the iconic series Streets of Rage comes back with a masterful tribute to and revitalization of the classic action fans adore.', 100, 5000, true, '2020-05-03 00:30:08.000000', 'https://steamcdn-a.akamaihd.net/steam/apps/985890/header.jpg?t=1588284729', 2);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (11, 'Dying light', 'First-person action survival game set in a post-apocalyptic open world overrun by flesh-hungry zombies. Roam a city devastated by a mysterious virus epidemic. Scavenge for supplies, craft weapons, and face hordes of the infected.', 145, 1000, true, '2020-05-03 12:41:59.000000', 'https://cdn-cf.gamivo.com/image_cover.jpg?f=143599&n=9191865769011647.jpg&h=763f90de7b6c420318aa2377ac2c1cd9', 2);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (10, 'PC', 'A personal computer (PC) is a multi-purpose computer whose size, capabilities, and price make it feasible for individual use. Personal computers are intended to be operated directly by an end user, rather than by a computer expert or technician. Unlike large costly minicomputers and mainframes, time-sharing by many people at the same time is not used with personal computers.', 100, 100000, true, '2020-05-02 13:56:18.000000', 'https://www.powerplanetonline.com/cdnassets/caja_pc_unykach_armor_evo_transparente_rgb_01_l.jpg', 4);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (7, 'Nintendo Switch', 'The Nintendo Switch[b] is a video game console developed by Nintendo, released worldwide in most regions on March 3, 2017. It is a hybrid console that can be used as a home console and portable device. Its wireless Joy-Con controllers, with standard buttons and directional analog sticks for user input, motion sensing, and tactile feedback, can attach to both sides of the console to support handheld-style play. They can also connect to a Grip accessory to provide a traditional home console gamepad form, or be used individually in the hand like the Wii Remote and Nunchuk, supporting local multiplayer modes. The Nintendo Switch''s software supports online gaming through Internet connectivity, as well as local wireless ad hoc connectivity with other consoles. Nintendo Switch games and software are available on both physical flash-based ROM cartridges and digital distribution via Nintendo eShop; the system has no region lockout. A handheld-focused revision of the system, called the Nintendo Switch Lite, was released on September 20, 2019.', 100, 10000, true, '2020-05-03 00:31:35.000000', 'https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Fsickr.files.wordpress.com%2F2017%2F08%2Fnintendo_switch_docked_console_and_red_and_blue_joy_con.jpg&f=1&nofb=1', 3);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (2, 'Garry''s mod', 'This game is about being able to do what ever the f**** you want.', 11111, 1500, false, '2020-05-03 00:20:50.000000', 'https://assets1.ignimgs.com/2016/01/03/gmodjpg-0d6f2d.jpg', 2);
INSERT INTO public.main_product (id, name, description, quantity, price, "onSale", release_date, product_display_image, manufacturer_id) VALUES (1, 'PlayStation 4', 'The PlayStation 4 (officially abbreviated as PS4) is an eighth-generation home video game console developed by Sony Interactive Entertainment. Announced as the successor to the PlayStation 3 in February 2013, it was launched on November 15 in North America, November 29 in Europe, South America and Australia, and on February 22, 2014 in Japan. It''s the 4th best-selling console of all time. It competes with Microsoft''s Xbox One and Nintendo''s Wii U and Switch.', 20, 60000, false, '2020-05-03 00:21:40.000000', 'https://external-content.duckduckgo.com/iu/?u=https%3A%2F%2Fwww.gamerbolt.com%2Fwp-content%2Fuploads%2F2014%2F11%2FPs4-Console-.jpg&f=1&nofb=1', 1);